import React, { useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchLatestDraws } from '../redux/actions/drawActions';

const Dashboard = () => {
  const { t } = useTranslation();
  const dispatch = useDispatch();
  const { latestDraw, loading, error } = useSelector(state => state.draws);

  useEffect(() => {
    if (!latestDraw) {
      dispatch(fetchLatestDraws());
    }
  }, [dispatch, latestDraw]);

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Welcome Card */}
        <div className="lg:col-span-2 bg-white dark:bg-gray-800 rounded-lg shadow-sm p-6">
          <h1 className="text-2xl font-bold mb-4">{t('welcomeTitle') || 'Bienvenue sur Euromillions Analyzer'}</h1>
          <p className="text-gray-600 dark:text-gray-300 mb-6">
            {t('welcomeMessage') || 'Analysez les tirages, générez des grilles et maximisez vos chances au Euromillions.'}
          </p>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <Link
              to="/generate"
              className="bg-blue-600 text-white py-3 px-4 rounded-lg hover:bg-blue-700 flex items-center justify-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
              </svg>
              {t('generateGrid') || 'Générer une grille'}
            </Link>
            
            <Link
              to="/statistics"
              className="bg-gray-100 dark:bg-gray-700 text-gray-800 dark:text-gray-200 py-3 px-4 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-600 flex items-center justify-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
              </svg>
              {t('viewStatistics') || 'Voir les statistiques'}
            </Link>
          </div>
        </div>
        
        {/* Latest Draw Card */}
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm p-6">
          <h2 className="text-xl font-semibold mb-4">{t('latestDraw') || 'Dernier tirage'}</h2>
          
          {loading ? (
            <div className="animate-pulse flex flex-col">
              <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-3/4 mb-3"></div>
              <div className="h-10 bg-gray-200 dark:bg-gray-700 rounded mb-3"></div>
              <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
            </div>
          ) : error ? (
            <div className="text-red-500 text-sm">
              {t('errorLoadingDraw') || 'Erreur lors du chargement du tirage.'}
            </div>
          ) : latestDraw ? (
            <div>
              <p className="text-sm text-gray-500 dark:text-gray-400 mb-2">
                {new Date(latestDraw.date).toLocaleDateString()}
              </p>
              
              <div className="flex justify-center my-4 space-x-2">
                {latestDraw.numbers.map(number => (
                  <div key={`number-${number}`} className="flex items-center justify-center w-10 h-10 rounded-full bg-blue-600 text-white font-bold">
                    {number}
                  </div>
                ))}
                <span className="self-center mx-2">+</span>
                {latestDraw.stars.map(star => (
                  <div key={`star-${star}`} className="flex items-center justify-center w-10 h-10 rounded-full bg-yellow-400 text-yellow-900 font-bold">
                    {star}
                  </div>
                ))}
              </div>
              
              <div className="text-center mt-4">
                <Link 
                  to="/draws" 
                  className="text-blue-600 dark:text-blue-400 hover:underline"
                >
                  {t('viewAllDraws') || 'Voir tous les tirages'}
                </Link>
              </div>
            </div>
          ) : (
            <div className="text-center py-4 text-gray-500 dark:text-gray-400">
              {t('noDrawData') || 'Aucune donnée de tirage disponible.'}
            </div>
          )}
        </div>
      </div>
      
      {/* Quick Links Section */}
      <div className="mt-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm p-6 hover:shadow-md transition-shadow">
          <h3 className="font-medium mb-2">
            {t('savedGrids') || 'Grilles sauvegardées'}
          </h3>
          <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
            {t('savedGridsDesc') || 'Accédez à vos grilles sauvegardées.'}
          </p>
          <Link 
            to="/saved-grids" 
            className="text-blue-600 dark:text-blue-400 text-sm hover:underline"
          >
            {t('openSavedGrids') || 'Ouvrir'} →
          </Link>
        </div>
        
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm p-6 hover:shadow-md transition-shadow">
          <h3 className="font-medium mb-2">
            {t('predictions') || 'Prédictions'}
          </h3>
          <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
            {t('predictionsDesc') || 'Consultez les prédictions pour le prochain tirage.'}
          </p>
          <Link 
            to="/predictions" 
            className="text-blue-600 dark:text-blue-400 text-sm hover:underline"
          >
            {t('openPredictions') || 'Ouvrir'} →
          </Link>
        </div>
        
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm p-6 hover:shadow-md transition-shadow">
          <h3 className="font-medium mb-2">
            {t('statistics') || 'Statistiques'}
          </h3>
          <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
            {t('statisticsDesc') || 'Explorez les statistiques détaillées des tirages.'}
          </p>
          <Link 
            to="/statistics" 
            className="text-blue-600 dark:text-blue-400 text-sm hover:underline"
          >
            {t('openStatistics') || 'Ouvrir'} →
          </Link>
        </div>
        
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-sm p-6 hover:shadow-md transition-shadow">
          <h3 className="font-medium mb-2">
            {t('settings') || 'Paramètres'}
          </h3>
          <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
            {t('settingsDesc') || 'Personnalisez votre expérience.'}
          </p>
          <Link 
            to="/settings" 
            className="text-blue-600 dark:text-blue-400 text-sm hover:underline"
          >
            {t('openSettings') || 'Ouvrir'} →
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
