// src/pages/LastDraw.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const LastDraw = () => {
  const navigate = useNavigate();
  const [latestDraw, setLatestDraw] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fonction pour charger le dernier tirage
    const fetchLatestDraw = async () => {
      setLoading(true);
      try {
        try {
          // Tenter de récupérer les données depuis l'API
          const response = await axios.get('/api/draws/latest');
          setLatestDraw(response.data);
        } catch (apiError) {
          console.warn('Fallback sur les données fictives:', apiError);
          
          // Créer un tirage fictif plus détaillé en cas d'erreur API
          const mockLatestDraw = {
            date: new Date().toISOString(),
            numbers: [5, 12, 23, 34, 45],
            stars: [2, 7],
            jackpot: "17 000 000 €",
            winners: 0,
            drawNumber: 1584,
            prizes: [
              { rank: 1, match: "5 numéros + 2 étoiles", winners: 0, amount: "Jackpot" },
              { rank: 2, match: "5 numéros + 1 étoile", winners: 2, amount: "824 032,90 €" },
              { rank: 3, match: "5 numéros + 0 étoile", winners: 6, amount: "34 899,50 €" },
              { rank: 4, match: "4 numéros + 2 étoiles", winners: 37, amount: "1 616,40 €" },
              { rank: 5, match: "4 numéros + 1 étoile", winners: 738, amount: "127,00 €" },
              { rank: 6, match: "3 numéros + 2 étoiles", winners: 1682, amount: "59,70 €" },
              { rank: 7, match: "4 numéros + 0 étoiles", winners: 1422, amount: "43,30 €" },
              { rank: 8, match: "2 numéros + 2 étoiles", winners: 24520, amount: "16,10 €" },
              { rank: 9, match: "3 numéros + 1 étoile", winners: 32068, amount: "12,60 €" },
              { rank: 10, match: "3 numéros + 0 étoiles", winners: 62874, amount: "10,30 €" },
              { rank: 11, match: "1 numéro + 2 étoiles", winners: 128453, amount: "8,10 €" },
              { rank: 12, match: "2 numéros + 1 étoile", winners: 458302, amount: "7,00 €" },
              { rank: 13, match: "2 numéros + 0 étoiles", winners: 919472, amount: "4,30 €" }
            ],
            country: "France",
            nextDrawDate: new Date(new Date().setDate(new Date().getDate() + 3)).toISOString(),
            nextJackpot: "26 000 000 €"
          };
          
          setLatestDraw(mockLatestDraw);
        }
        
        setError(null);
      } catch (err) {
        console.error("Erreur lors du chargement du dernier tirage:", err);
        setError("Impossible de charger les informations sur le dernier tirage");
      } finally {
        setLoading(false);
      }
    };
    
    fetchLatestDraw();
  }, []);

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const shareResults = () => {
    if (latestDraw) {
      const shareText = `Résultats EuroMillions du ${formatDate(latestDraw.date)}: 
        Numéros: ${latestDraw.numbers.join(' - ')} 
        Étoiles: ${latestDraw.stars.join(' - ')}
        Jackpot: ${latestDraw.jackpot}`;
      
      if (navigator.share) {
        navigator.share({
          title: 'Résultats EuroMillions',
          text: shareText,
          url: window.location.href,
        })
        .catch((error) => console.log('Erreur de partage', error));
      } else {
        // Fallback pour les navigateurs qui ne supportent pas l'API Web Share
        navigator.clipboard.writeText(shareText)
          .then(() => alert('Résultats copiés dans le presse-papier !'))
          .catch(() => alert('Impossible de copier le texte. Veuillez réessayer.'));
      }
    }
  };

  return (
    <div className="container">
      <h1 className="text-center">Dernier Tirage EuroMillions</h1>
      
      {loading ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Chargement des résultats...</p>
        </div>
      ) : error ? (
        <div className="error-alert">
          {error}
          <button 
            style={{marginLeft: '20px', padding: '5px 10px', border: 'none', background: '#c62828', color: 'white', borderRadius: '4px', cursor: 'pointer'}}
            onClick={() => window.location.reload()}
          >
            Réessayer
          </button>
        </div>
      ) : latestDraw ? (
        <>
          {/* Informations principales du tirage */}
          <div className="last-draw" style={{marginBottom: '30px'}}>
            <div className="grid-container" style={{marginTop: '0'}}>
              <div style={{gridColumn: '1 / span 2'}}>
                <h3>Tirage n°{latestDraw.drawNumber} du {formatDate(latestDraw.date)}</h3>
                
                <div style={{marginTop: '20px'}}>
                  <h4>Numéros gagnants :</h4>
                  <div className="numbers-container">
                    {latestDraw.numbers.map(num => (
                      <div key={`num-${num}`} className="number-ball" style={{width: '50px', height: '50px', fontSize: '1.25rem'}}>{num}</div>
                    ))}
                  </div>
                  
                  <h4>Étoiles :</h4>
                  <div className="numbers-container">
                    {latestDraw.stars.map(star => (
                      <div key={`star-${star}`} className="star-ball" style={{width: '50px', height: '50px', fontSize: '1.25rem'}}>{star}</div>
                    ))}
                  </div>
                </div>
              </div>
              
              <div style={{
                padding: '20px', 
                backgroundColor: '#f5f5f5', 
                borderRadius: '8px',
                border: '1px solid #e0e0e0'
              }}>
                <h3 style={{color: '#0a1f44', marginTop: '0'}}>Jackpot</h3>
                <h2 style={{margin: '10px 0', fontWeight: 'bold'}}>{latestDraw.jackpot}</h2>
                <p style={{color: '#666'}}>
                  {latestDraw.winners === 0 
                    ? "Aucun gagnant au rang 1"
                    : `${latestDraw.winners} gagnant(s) au rang 1`
                  }
                </p>
                
                <hr style={{margin: '15px 0', border: 'none', borderTop: '1px solid #ddd'}} />
                
                <h4>Prochain tirage : {formatDate(latestDraw.nextDrawDate)}</h4>
                <h3 style={{color: '#0a1f44', fontWeight: 'bold'}}>
                  Jackpot estimé : {latestDraw.nextJackpot}
                </h3>
                
                <button 
                  className="card-button" 
                  style={{marginTop: '15px', width: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px'}}
                  onClick={shareResults}
                >
                  <span>↗️</span> Partager ces résultats
                </button>
              </div>
            </div>
          </div>
          
          {/* Tableau des gains */}
          <h2>Détails des gains</h2>
          
          <div style={{
            overflowX: 'auto',
            backgroundColor: 'white',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
            marginBottom: '30px'
          }}>
            <table style={{width: '100%', borderCollapse: 'collapse'}}>
              <thead>
                <tr style={{backgroundColor: '#e3f2fd'}}>
                  <th style={{padding: '12px', textAlign: 'left', borderBottom: '1px solid #e0e0e0'}}>Rang</th>
                  <th style={{padding: '12px', textAlign: 'left', borderBottom: '1px solid #e0e0e0'}}>Combinaison</th>
                  <th style={{padding: '12px', textAlign: 'center', borderBottom: '1px solid #e0e0e0'}}>Nombre de gagnants</th>
                  <th style={{padding: '12px', textAlign: 'right', borderBottom: '1px solid #e0e0e0'}}>Gain par gagnant</th>
                </tr>
              </thead>
              <tbody>
                {latestDraw.prizes.map((prize) => (
                  <tr 
                    key={prize.rank}
                    style={{ 
                      backgroundColor: prize.rank % 2 === 0 ? 'white' : '#f9f9f9',
                      ...(prize.rank === 1 && { backgroundColor: '#e8f5e9' })
                    }}
                  >
                    <td style={{padding: '12px', borderBottom: '1px solid #e0e0e0'}}>{prize.rank}</td>
                    <td style={{padding: '12px', borderBottom: '1px solid #e0e0e0'}}>{prize.match}</td>
                    <td style={{padding: '12px', textAlign: 'center', borderBottom: '1px solid #e0e0e0'}}>
                      {prize.winners.toLocaleString('fr-FR')}
                    </td>
                    <td style={{padding: '12px', textAlign: 'right', borderBottom: '1px solid #e0e0e0', fontWeight: prize.rank === 1 ? 'bold' : 'normal'}}>
                      {prize.amount}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          {/* Vérifier vos grilles et historique */}
          <div className="grid-container">
            <div className="card">
              <h3>Vérifier vos grilles</h3>
              <p style={{color: '#666'}}>
                Vous avez joué à ce tirage ? Vérifiez si vos numéros sont gagnants !
              </p>
              <div className="card-action">
                <button 
                  className="card-button"
                  style={{backgroundColor: '#0a1f44', color: 'white'}}
                  onClick={() => navigate('/mes-grilles')}
                >
                  Vérifier mes grilles
                </button>
              </div>
            </div>
            
            <div className="card">
              <h3>Consultez l'historique</h3>
              <p style={{color: '#666'}}>
                Accédez à l'ensemble des tirages précédents et analysez les tendances.
              </p>
              <div className="card-action">
                <button 
                  className="card-button"
                  onClick={() => navigate('/historique')}
                >
                  Voir l'historique
                </button>
              </div>
            </div>
          </div>
          
          {/* Notes et informations */}
          <div style={{
            marginTop: '30px',
            padding: '15px',
            backgroundColor: '#f5f5f5',
            borderRadius: '8px',
            color: '#666',
            fontSize: '14px'
          }}>
            <p>
              Note : Les informations présentées sont à titre indicatif uniquement. 
              Pour les résultats officiels, veuillez consulter le site officiel d'EuroMillions.
            </p>
          </div>
        </>
      ) : (
        <p>Aucune information disponible sur le dernier tirage</p>
      )}
    </div>
  );
};

export default LastDraw;
