// src/pages/Predictions.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Predictions = () => {
  const [currentPrediction, setCurrentPrediction] = useState(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [generationError, setGenerationError] = useState(null);

  // Fonction pour r√©cup√©rer une pr√©diction
  const generatePrediction = async () => {
    setIsGenerating(true);
    setGenerationError(null);
    
    try {
      try {
        // Tenter de r√©cup√©rer les donn√©es depuis l'API
        const response = await axios.get('/api/predictions/generate');
        setCurrentPrediction(response.data);
      } catch (apiError) {
        console.warn('Fallback sur les donn√©es fictives:', apiError);
        
        // Simuler un appel API (√† remplacer par votre vrai appel API)
        // Attendre 2 secondes pour simuler le chargement
        await new Promise(resolve => setTimeout(resolve, 2000));
        
        // G√©n√©rer 5 nombres al√©atoires entre 1 et 50 (sans doublons)
        const allNumbers = Array.from({ length: 50 }, (_, i) => i + 1);
        const numbers = [];
        for (let i = 0; i < 5; i++) {
          const randomIndex = Math.floor(Math.random() * allNumbers.length);
          numbers.push(allNumbers[randomIndex]);
          allNumbers.splice(randomIndex, 1);
        }
        numbers.sort((a, b) => a - b);
        
        // G√©n√©rer 2 √©toiles al√©atoires entre 1 et 12 (sans doublons)
        const allStars = Array.from({ length: 12 }, (_, i) => i + 1);
        const stars = [];
        for (let i = 0; i < 2; i++) {
          const randomIndex = Math.floor(Math.random() * allStars.length);
          stars.push(allStars[randomIndex]);
          allStars.splice(randomIndex, 1);
        }
        stars.sort((a, b) => a - b);
        
        // Cr√©er un objet pr√©diction
        const prediction = {
          id: Date.now().toString(),
          numbers,
          stars,
          date: new Date().toISOString(),
          confidence: Math.floor(Math.random() * 100)
        };
        
        setCurrentPrediction(prediction);
      }
    } catch (err) {
      console.error('Erreur lors de la g√©n√©ration de la pr√©diction:', err);
      setGenerationError('Impossible de g√©n√©rer une pr√©diction pour le moment');
    } finally {
      setIsGenerating(false);
    }
  };

  // G√©n√©rer une pr√©diction au chargement initial
  useEffect(() => {
    generatePrediction();
  }, []);

  return (
    <div className="container">
      <h1 className="text-center">Pr√©dictions pour le prochain tirage</h1>
      
      <p className="text-center">
        Notre algorithme analyse les tendances des tirages pass√©s pour vous proposer des combinaisons.
        Ces pr√©dictions sont bas√©es sur l'analyse statistique et ne garantissent pas de gains.
      </p>
      
      <div className="text-center" style={{margin: '30px 0'}}>
        <button 
          className="play-button"
          onClick={generatePrediction}
          disabled={isGenerating}
          style={{
            display: 'inline-flex',
            alignItems: 'center',
            gap: '10px',
            fontSize: '16px',
            padding: '10px 20px',
            opacity: isGenerating ? 0.7 : 1
          }}
        >
          {isGenerating ? (
            <>
              <div className="loading-spinner" style={{width: '20px', height: '20px', borderWidth: '2px'}}></div>
              G√©n√©ration en cours...
            </>
          ) : (
            <>
              <span>üîÑ</span> G√©n√©rer une nouvelle pr√©diction
            </>
          )}
        </button>
      </div>
      
      {generationError && (
        <div className="error-alert">
          {generationError}
        </div>
      )}
      
      {isGenerating ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Calcul des num√©ros en cours...</p>
        </div>
      ) : (
        currentPrediction && (
          <div style={{maxWidth: '600px', margin: '0 auto'}}>
            <div className="card" style={{textAlign: 'center'}}>
              <h3>Combinaison sugg√©r√©e</h3>
              
              <div style={{marginTop: '20px'}}>
                <h4>Num√©ros :</h4>
                <div style={{display: 'flex', justifyContent: 'center', gap: '10px', marginBottom: '20px'}}>
                  {currentPrediction.numbers.map(num => (
                    <div 
                      key={`pred-num-${num}`} 
                      className="number-ball"
                      style={{width: '50px', height: '50px', fontSize: '1.25rem'}}
                    >
                      {num}
                    </div>
                  ))}
                </div>
                
                <h4>√âtoiles :</h4>
                <div style={{display: 'flex', justifyContent: 'center', gap: '10px'}}>
                  {currentPrediction.stars.map(star => (
                    <div 
                      key={`pred-star-${star}`} 
                      className="star-ball"
                      style={{width: '50px', height: '50px', fontSize: '1.25rem'}}
                    >
                      {star}
                    </div>
                  ))}
                </div>
              </div>
              
              <div style={{
                marginTop: '30px',
                padding: '15px',
                backgroundColor: '#f5f5f5',
                borderRadius: '8px',
                color: '#666'
              }}>
                <p>
                  Cette pr√©diction a √©t√© g√©n√©r√©e le {new Date(currentPrediction.date).toLocaleString()}
                </p>
                <p style={{fontWeight: 'bold'}}>
                  Indice de confiance: {currentPrediction.confidence}%
                </p>
                
                <div 
                  style={{
                    height: '10px',
                    backgroundColor: '#e0e0e0',
                    borderRadius: '5px',
                    margin: '10px 0',
                    position: 'relative',
                    overflow: 'hidden'
                  }}
                >
                  <div 
                    style={{
                      height: '100%',
                      width: `${currentPrediction.confidence}%`,
                      backgroundColor: currentPrediction.confidence < 30 ? '#f44336' : 
                                       currentPrediction.confidence < 70 ? '#ffc107' : '#4caf50',

                      borderRadius: '5px',
                      transition: 'width 0.3s'
                    }}
                  />
                </div>
              </div>
              
              <div style={{marginTop: '20px'}}>
                <button className="card-button">
                  Sauvegarder cette grille
                </button>
              </div>
            </div>
            
            <div style={{marginTop: '40px'}}>
              <h3>Comment fonctionnent nos pr√©dictions ?</h3>
              <p>
                Notre syst√®me analyse les donn√©es historiques des tirages EuroMillions depuis 2004.
                Nous utilisons des algorithmes d'apprentissage automatique qui prennent en compte
                la fr√©quence d'apparition des num√©ros, les tendances r√©centes, et divers mod√®les statistiques.
              </p>
              <p>
                L'indice de confiance repr√©sente une estimation de la qualit√© de la pr√©diction bas√©e
                sur plusieurs facteurs, notamment la coh√©rence avec les mod√®les historiques.
              </p>
              <p style={{fontWeight: 'bold', marginTop: '20px', padding: '10px', backgroundColor: '#fff3e0', borderRadius: '8px'}}>
                Rappel : Le jeu d'EuroMillions est un jeu de hasard. Ces pr√©dictions sont fournies
                √† titre informatif uniquement et ne garantissent aucunement un gain.
              </p>
            </div>
          </div>
        )
      )}
    </div>
  );
};

export default Predictions;
