// src/pages/DrawHistory.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DrawHistory = () => {
  const [draws, setDraws] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [jackpotMin, setJackpotMin] = useState('');
  const [year, setYear] = useState('all');

  // Années disponibles pour le filtre (de 2004 à maintenant)
  const years = Array.from(
    { length: new Date().getFullYear() - 2003 },
    (_, i) => 2004 + i
  ).sort((a, b) => b - a); // Tri décroissant

  useEffect(() => {
    // Fonction pour charger l'historique des tirages
    const fetchDrawHistory = async () => {
      setLoading(true);
      try {
        try {
          // Tenter de récupérer les données depuis l'API
          const response = await axios.get('/api/draws');
          setDraws(response.data.draws || []);
        } catch (apiError) {
          console.warn('Fallback sur les données fictives:', apiError);
          
          // Générer des données fictives pour l'historique des tirages
          const mockDraws = Array.from({ length: 50 }, (_, i) => {
            // Date fictive : 50 tirages précédents, 2 par semaine (mardi et vendredi)
            const date = new Date();
            date.setDate(date.getDate() - Math.floor(i / 2) * 3.5 - (i % 2 === 0 ? 0 : 3));
            
            return {
              id: 1584 - i,
              date: date.toISOString(),
              numbers: Array.from(
                { length: 5 },
                () => Math.floor(Math.random() * 50) + 1
              ).sort((a, b) => a - b),
              stars: Array.from(
                { length: 2 },
                () => Math.floor(Math.random() * 12) + 1
              ).sort((a, b) => a - b),
              jackpot: `${Math.floor(Math.random() * 150) + 17} 000 000 €`,
              winners: Math.random() < 0.2 ? Math.floor(Math.random() * 3) + 1 : 0,
              dayOfWeek: date.getDay() === 2 ? 'Mardi' : 'Vendredi'
            };
          });
          
          setDraws(mockDraws);
        }
        
        setError(null);
      } catch (err) {
        console.error("Erreur lors du chargement de l'historique des tirages:", err);
        setError("Impossible de charger l'historique des tirages");
      } finally {
        setLoading(false);
      }
    };
    
    fetchDrawHistory();
  }, []);

  // Filtrer les tirages selon les critères sélectionnés
  const filteredDraws = draws.filter(draw => {
    const drawDate = new Date(draw.date);
    const drawYear = drawDate.getFullYear();
    
    // Filtre par année
    if (year !== 'all' && drawYear !== parseInt(year)) {
      return false;
    }
    
    // Filtre par date de début
    if (startDate) {
      const startDateObj = new Date(startDate);
      if (drawDate < startDateObj) {
        return false;
      }
    }
    
    // Filtre par date de fin
    if (endDate) {
      const endDateObj = new Date(endDate);
      endDateObj.setHours(23, 59, 59, 999);
      if (drawDate > endDateObj) {
        return false;
      }
    }
    
    // Filtre par montant minimum du jackpot
    if (jackpotMin) {
      const jackpotValue = parseInt(draw.jackpot.replace(/[^\d]/g, ''));
      if (jackpotValue < parseInt(jackpotMin) * 1000000) {
        return false;
      }
    }
    
    return true;
  });

  const handleChangePage = (newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const resetFilters = () => {
    setStartDate('');
    setEndDate('');
    setJackpotMin('');
    setYear('all');
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  // Pagination manuelle
  const totalPages = Math.ceil(filteredDraws.length / rowsPerPage);
  const paginatedDraws = filteredDraws.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage);

  return (
    <div className="container">
      <h1 className="text-center">Historique des Tirages EuroMillions</h1>
      
      <p className="text-center">
        Consultez tous les résultats des tirages précédents d'EuroMillions depuis 2004.
      </p>
      
      {/* Filtres */}
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '12px',
        boxShadow: '0 3px 12px rgba(0, 0, 0, 0.1)',
        marginBottom: '30px'
      }}>
        <h3 style={{marginTop: 0}}>Filtrer les résultats</h3>
        
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
          gap: '20px',
          marginBottom: '20px'
        }}>
          <div>
            <label style={{display: 'block', marginBottom: '8px', fontWeight: '500'}}>Année</label>
            <select 
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #ddd',
                borderRadius: '8px',
                fontSize: '16px'
              }}
              value={year}
              onChange={(e) => setYear(e.target.value)}
            >
              <option value="all">Toutes les années</option>
              {years.map(y => (
                <option key={y} value={y}>{y}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label style={{display: 'block', marginBottom: '8px', fontWeight: '500'}}>Date de début</label>
            <input 
              type="date" 
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #ddd',
                borderRadius: '8px',
                fontSize: '16px'
              }}
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
          </div>
          
          <div>
            <label style={{display: 'block', marginBottom: '8px', fontWeight: '500'}}>Date de fin</label>
            <input 
              type="date" 
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #ddd',
                borderRadius: '8px',
                fontSize: '16px'
              }}
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </div>
          
          <div>
            <label style={{display: 'block', marginBottom: '8px', fontWeight: '500'}}>Jackpot minimum (M€)</label>
            <input 
              type="number" 
              style={{
                width: '100%',
                padding: '12px',
                border: '1px solid #ddd',
                borderRadius: '8px',
                fontSize: '16px'
              }}
              value={jackpotMin}
              onChange={(e) => setJackpotMin(e.target.value)}
              min="0"
            />
          </div>
        </div>
        
        <div style={{display: 'flex', justifyContent: 'flex-end'}}>
          <button 
            className="card-button"
            onClick={resetFilters}
            style={{marginRight: '10px'}}
          >
            Réinitialiser
          </button>
        </div>
      </div>
      
      {/* Résultats */}
      {loading ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Chargement de l'historique des tirages...</p>
        </div>
      ) : error ? (
        <div className="error-alert">
          {error}
          <button 
            style={{marginLeft: '20px', padding: '5px 10px', border: 'none', background: '#c62828', color: 'white', borderRadius: '4px', cursor: 'pointer'}}
            onClick={() => window.location.reload()}
          >
            Réessayer
          </button>
        </div>
      ) : (
        <>
          <p><strong>{filteredDraws.length}</strong> résultats trouvés</p>
          
          <div style={{
            overflowX: 'auto',
            backgroundColor: 'white',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
            marginBottom: '20px'
          }}>
            <table style={{width: '100%', borderCollapse: 'collapse'}}>
              <thead>
                <tr style={{backgroundColor: '#e3f2fd'}}>
                  <th style={{padding: '12px', textAlign: 'left', borderBottom: '1px solid #e0e0e0'}}>Date</th>
                  <th style={{padding: '12px', textAlign: 'left', borderBottom: '1px solid #e0e0e0'}}>N° Tirage</th>
                  <th style={{padding: '12px', textAlign: 'left', borderBottom: '1px solid #e0e0e0'}}>Numéros</th>
                  <th style={{padding: '12px', textAlign: 'left', borderBottom: '1px solid #e0e0e0'}}>Étoiles</th>
                  <th style={{padding: '12px', textAlign: 'left', borderBottom: '1px solid #e0e0e0'}}>Jackpot</th>
                  <th style={{padding: '12px', textAlign: 'center', borderBottom: '1px solid #e0e0e0'}}>Gagnant(s) Rang 1</th>
                </tr>
              </thead>
              <tbody>
                {paginatedDraws.map((draw) => (
                  <tr 
                    key={draw.id}
                    style={{ backgroundColor: draw.id % 2 === 0 ? 'white' : '#f9f9f9' }}
                  >
                    <td style={{padding: '12px', borderBottom: '1px solid #e0e0e0'}}>
                      <div>
                        <div>{formatDate(draw.date)}</div>
                        <div style={{color: '#666', fontSize: '14px'}}>{draw.dayOfWeek}</div>
                      </div>
                    </td>
                    <td style={{padding: '12px', borderBottom: '1px solid #e0e0e0'}}>{draw.id}</td>
                    <td style={{padding: '12px', borderBottom: '1px solid #e0e0e0'}}>
                      <div style={{display: 'flex', gap: '4px', flexWrap: 'wrap'}}>
                        {draw.numbers.map(num => (
                          <span
                            key={`num-${draw.id}-${num}`}
                            style={{
                              display: 'inline-block',
                              width: '28px',
                              height: '28px',
                              borderRadius: '50%',
                              backgroundColor: '#0a1f44',
                              color: 'white',
                              textAlign: 'center',
                              lineHeight: '28px',
                              fontSize: '14px',
                              fontWeight: 'bold'
                            }}
                          >
                            {num}
                          </span>
                        ))}
                      </div>
                    </td>
                    <td style={{padding: '12px', borderBottom: '1px solid #e0e0e0'}}>
                      <div style={{display: 'flex', gap: '4px'}}>
                        {draw.stars.map(star => (
                          <span
                            key={`star-${draw.id}-${star}`}
                            style={{
                              display: 'inline-block',
                              width: '28px',
                              height: '28px',
                              borderRadius: '50%',
                              backgroundColor: '#ffc107',
                              color: 'black',
                              textAlign: 'center',
                              lineHeight: '28px',
                              fontSize: '14px',
                              fontWeight: 'bold'
                            }}
                          >
                            {star}
                          </span>
                        ))}
                      </div>
                    </td>
                    <td style={{padding: '12px', borderBottom: '1px solid #e0e0e0'}}>{draw.jackpot}</td>
                    <td style={{padding: '12px', textAlign: 'center', borderBottom: '1px solid #e0e0e0'}}>
                      {draw.winners === 0 
                        ? <span style={{color: '#666'}}>Aucun</span>
                        : <span style={{fontWeight: 'bold'}}>{draw.winners}</span>
                      }
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          {/* Pagination */}
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            padding: '10px',
            backgroundColor: 'white',
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)'
          }}>
            <div>
              <select
                style={{
                  padding: '8px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  marginRight: '10px'
                }}
                value={rowsPerPage}
                onChange={handleChangeRowsPerPage}
              >
                <option value={10}>10 lignes</option>
                <option value={25}>25 lignes</option>
                <option value={50}>50 lignes</option>
              </select>
              <span>
                lignes par page
              </span>
            </div>
            
            <div style={{display: 'flex', alignItems: 'center'}}>
              <button
                style={{
                  padding: '8px 12px',
                  border: 'none',
                  background: '#0a1f44',
                  color: 'white',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  marginRight: '10px',
                  opacity: page === 0 ? 0.5 : 1
                }}
                onClick={() => handleChangePage(page - 1)}
                disabled={page === 0}
              >
                Précédent
              </button>
              
              <span>
                Page {page + 1} sur {totalPages || 1}
              </span>
              
              <button
                style={{
                  padding: '8px 12px',
                  border: 'none',
                  background: '#0a1f44',
                  color: 'white',
                  borderRadius: '4px',
                  cursor: 'pointer',
                  marginLeft: '10px',
                  opacity: page >= totalPages - 1 ? 0.5 : 1
                }}
                onClick={() => handleChangePage(page + 1)}
                disabled={page >= totalPages - 1}
              >
                Suivant
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default DrawHistory;
