// src/App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Import des pages
import Home from './pages/Home';
import LastDraw from './pages/LastDraw';
import DrawHistory from './pages/DrawHistory';
import Statistics from './pages/Statistics';
import Predictions from './pages/Predictions';
import SavedGrids from './pages/SavedGrids';
import NotFound from './pages/NotFound';

// Import des composants
import Layout from './components/Layout';

// Import des services
import { checkForUpdates } from './services/updateService';

// Composant principal de l'application
function App() {
  // État pour gérer les mises à jour disponibles
  const [updateAvailable, setUpdateAvailable] = useState(false);
  
  // Vérifier les mises à jour au chargement
  useEffect(() => {
    const checkUpdates = async () => {
      try {
        const hasUpdate = await checkForUpdates();
        if (hasUpdate) {
          setUpdateAvailable(true);
          toast.info('Une mise à jour des résultats est disponible !', {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
          });
        }
      } catch (error) {
        console.error('Erreur lors de la vérification des mises à jour:', error);
      }
    };
    
    // Lancer la vérification des mises à jour
    checkUpdates();
    
    // Vérifier les mises à jour toutes les heures
    const updateInterval = setInterval(checkUpdates, 3600000);
    
    // Nettoyer l'intervalle lors du démontage du composant
    return () => clearInterval(updateInterval);
  }, []);
  
  return (
    <Router>
      <ToastContainer 
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      
      <Routes>
        <Route path="/" element={<Layout updateAvailable={updateAvailable} />}>
          <Route index element={<Home />} />
          <Route path="last-draw" element={<LastDraw />} />
          <Route path="history" element={<DrawHistory />} />
          <Route path="statistics" element={<Statistics />} />
          <Route path="predictions" element={<Predictions />} />
          <Route path="saved-grids" element={<SavedGrids />} />
          <Route path="404" element={<NotFound />} />
          <Route path="*" element={<Navigate to="/404" replace />} />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
