// components/common/Sidebar.js
import React from 'react';
import { NavLink } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

// Import icons (ajustez selon votre bibliothèque d'icônes)
import { 
  HomeIcon, 
  ArchiveIcon, 
  TicketIcon,
  BookmarkIcon,
  ChartBarIcon, 
  LightningBoltIcon, 
  CogIcon 
} from '@heroicons/react/outline';

const Sidebar = ({ isOpen, toggleSidebar }) => {
  const { t } = useTranslation();
  
  const menuItems = [
    { name: t('dashboard'), path: '/', icon: HomeIcon },
    { name: t('drawHistory'), path: '/draws', icon: ArchiveIcon },
    { name: t('generateGrids'), path: '/generate', icon: TicketIcon },
    { name: t('savedGrids'), path: '/saved-grids', icon: BookmarkIcon }, // Nouveau lien
    { name: t('predictions'), path: '/predictions', icon: LightningBoltIcon },
    { name: t('statistics'), path: '/statistics', icon: ChartBarIcon },
    { name: t('settings'), path: '/settings', icon: CogIcon },
  ];

  return (
    <aside className={`fixed inset-y-0 left-0 z-50 w-64 bg-white dark:bg-gray-800 shadow-md transform ${isOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out md:translate-x-0`}>
      <div className="h-full flex flex-col">
        {/* Logo/App Name */}
        <div className="px-4 py-6 border-b border-gray-200 dark:border-gray-700">
          <h1 className="text-xl font-bold text-blue-600 dark:text-blue-400">{t('appName')}</h1>
          <button
            className="absolute top-5 right-4 md:hidden"
            onClick={toggleSidebar}
          >
            <svg className="h-6 w-6 text-gray-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        
        {/* Menu Items */}
        <nav className="flex-1 overflow-y-auto py-4">
          <ul className="px-2 space-y-1">
            {menuItems.map((item) => {
              const Icon = item.icon;
              return (
                <li key={item.path}>
                  <NavLink
                    to={item.path}
                    className={({ isActive }) => 
                      `flex items-center px-4 py-3 text-sm font-medium rounded-md transition-colors ${
                        isActive
                          ? 'bg-blue-50 text-blue-700 dark:bg-blue-900 dark:text-blue-200'
                          : 'text-gray-700 hover:bg-gray-100 dark:text-gray-300 dark:hover:bg-gray-700'
                      }`
                    }
                  >
                    <Icon className="mr-3 h-5 w-5" />
                    {item.name}
                  </NavLink>
                </li>
              );
            })}
          </ul>
        </nav>
        
        {/* Footer */}
        <div className="p-4 border-t border-gray-200 dark:border-gray-700 text-xs text-gray-500 dark:text-gray-400">
          <p>© {new Date().getFullYear()} {t('appName')}</p>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
